$(document).on('change load', '#options', function() {
  var customEvent = $('#custom-event');
  var training = $('#training');
  var transfer = $('#transfer');
  var visit = $('#visit');
  var x = parseInt($('#options').val());
  console.log(x);
  switch(x) {
    case 1:
      $.when(hide(training, transfer, visit)).done(customEvent.slideDown());
      break;
    case 2:
      $.when(hide(customEvent, transfer, visit)).done(training.slideDown());
      break;
    case 3:
      $.when(hide(customEvent, training, visit)).done(transfer.slideDown());
      break;
    case 4:
      $.when(hide(customEvent, training, transfer)).done(visit.slideDown());
      break;
    default:
      hide(customEvent, training, transfer, visit);
  }
});

$(document).on('change load', '#training-other-checkbox', function() {
  if ( $("#training-other-checkbox").is(':checked')) {
    $('.event_training_other').slideDown();
  } else {
    $('.event_training_other').slideUp();
  }
});


$(document).on('change', '[name="dog_id"]', function() {
  var data = $(this).serializeArray();
  $.ajax({
    method: 'POST',
    url: '/events/transfer',
    data: data,
    dataType: 'script'
  });
});


var initialize_calendar;
initialize_calendar = function() {
  $('.calendar').each(function() {
    var calendar = $(this);
    calendar.fullCalendar({
     
      header: {
        left: 'prev,next today',
        center: 'title',
        right: 'month,agendaWeek,agendaDay'
      },
      selectable: true,
      selectHelper: true,
      editable: true,
      eventLimit: true,
      events: '/events.json',
      timeFormat: 'HH:mm',

      select: function(start, end) {
        $.getScript('/events/new', function() {
          var startDate = moment(start).format("YYYY-MM-DD HH:mm");
          var endDate = moment(end).format("YYYY-MM-DD HH:mm");
          $('input#startTime').datetimepicker({
            defaultDate: startDate,
            format: 'YYYY-MM-DD HH:mm'
          });
          $('input#endTime').datetimepicker({
            defaultDate: endDate,
            format: 'YYYY-MM-DD HH:mm'
          });
          $('#startTime').val(moment(start).format("YYYY-MM-DD HH:mm"));
          $('#endTime').val(moment(end).format("YYYY-MM-DD HH:mm"));
        });

        calendar.fullCalendar('unselect');
      },


     eventDrop: function(event, delta, revertFunc) {
        event_data = { 
          event: {
            id: event.id,
            start: event.start.format(),
            end: event.end.format()
          }
        };
        $.ajax({
            url: event.update_url,
            data: event_data,
            type: 'PATCH'
        });
      },

      eventClick: function(event, jsEvent, view) {
        $.getScript(event.edit_url, function() {
          var startDate = moment(event.start).format("YYYY-MM-DD HH:mm");
          var endDate = moment(event.end).format("YYYY-MM-DD HH:mm");
          $('input#startTime').datetimepicker({
            defaultDate: startDate,
            format: 'YYYY-MM-DD HH:mm'
          });
          $('input#endTime').datetimepicker({
            defaultDate: endDate, 
            format: 'YYYY-MM-DD HH:mm'
          });
          $('#startTime').val(moment(event.start).format("YYYY-MM-DD HH:mm"));
          $('#endTime').val(moment(event.end).format("YYYY-MM-DD HH:mm"));
        });
      },

    });
  });
};
